(()=>{console.log(tempData);const n=document.querySelector("main");tempData.forEach((t=>{const e=document.createElement("section");e.classList.add("detalle-pelicula"),e.innerHTML=`\n    <div class="imagen-pelicula">\n        <img src="${t.poster}" alt="${t.title}">\n    </div>\n    <div class="info-pelicula">\n        <h2>${t.title}</h2>\n        <p><strong>Año:</strong> ${t.year}</p>\n        <p><strong>Género:</strong> ${t.genre.join(", ")}</p>\n        <p><strong>Duración:</strong> ${t.duration}</p>\n        <p><strong>Director:</strong> ${t.director}</p>\n        <p><strong>Valoración:</strong> ⭐${t.rate} / 10</p>\n    </div>\n    `,n.appendChild(e)}))})();